import './App.css';
import React, { useEffect, Component } from "react";
import Home from './Home/Home.js';
import Header from './Home/Header/Header.js'
import MenuScreen from './Menu/MenuScreen.js';
import RewardsScreen from './Rewards/RewardsScreen.js';
import GiftScreen from './Gifts/GiftScreen.js';
import { Button} from 'react-bootstrap';
import Footer from './Home/Footer/Footer.js';
import axios from 'axios';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import { ThemeConsumer } from 'react-bootstrap/esm/ThemeProvider';

class App extends Component {

  state = {
    isLoading: true,
    orders: []
  };

  async componentDidMount() {
    const order = await axios.get('http://localhost:8080/api/orders')
      .then(response => this.setState({orders: response, isLoading: false}));
  }

  async addOrder(id) {
   const response = await fetch(`/api/order/register/${id}`, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: "drink=Cappucino&milk=whole&size=Grande",
    });
    const body = axios.get('http://localhost:8080/api/orders');
    this.setState({orders: body, isLoading: false});
  }



  render() {

    const { orders, isLoading } = this.state;
    isLoading = false;

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (
      <div className="App">
        <Router>
          <Switch>
            <Route exact path="/">
              <Header></Header>
              {orders.map(order =>
                <div key={order.id}>
                  {order.total}
                </div>
              )}
              <Home></Home>
              <Footer></Footer>
            </Route>
            <Route exact path="/menu">
              <Header></Header>
              <MenuScreen />
              <Footer></Footer>
            </Route>
            <Route exact path="/gifts">
              <Header></Header>
              <GiftScreen />
              <Footer></Footer>
            </Route>
            <Route exact path="/rewards">
              <Header></Header>
              <Button size="sm" color="danger" onClick={() => this.addOrder(123)}>add</Button>
              {orders.map(order =>
                <div key={order.id}>
                  {order.total}
                </div>
              )}
              <RewardsScreen />
              <Footer></Footer>
            </Route>
          </Switch>
        </Router>
      </div>
    );
  }
}

export default App;
