import './Order.css'
import { Fade } from 'react-awesome-reveal'
import { Button } from 'react-bootstrap';


class Order extends Component {

    state = {
        isLoading: true,
        orders: []
    };

    async componentDidMount() {
        const response = await fetch('/api/orders');
        const body = await response.json();
        this.setState({ orders: body, isLoading: false });
    }


    async addOrder(id) {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ drink: "Caffe Latte", milk: "Whole", size: "Tall" })
        };
        const r = await fetch(`/api/order/register/${id}`, requestOptions);
        const body = await r.json();
        this.setState({ result: body, isLoading: false });
    }

    render() {

        const { orders, isLoading } = this.state;

        return (
            <Fade><p>here</p>
                <Button className='order' onClick={() => this.addOrder(123)}>
                    <img src={this.props.image} alt='' className='orer_image' />
                    <h4 className='order_type'>{this.props.type}</h4>
                </Button>
                <p>here again</p>
            </Fade>
        );
    }
}

export default Order
